import './App.css';

function App() {
  const src ="https://cdn.pixabay.com/photo/2021/07/20/14/59/iron-man-6480952_640.jpg" ;
  return (
    <>
      <div className='h-screen flex justify-center items-center bg-gradient-to-r from-purple-500 to-pink-500'>
        <div className='bg-cyan-500 rounded-lg p-9 pr-3 sm:pr-20 pl-3 sm:pl-20 flex flex-col items-center'>
        <div>
          <img src ={src} alt='avatar' className='rounded-[50%] ring-4 ring-green-600 ring-offset-4 ring-offset-white hover:scale-110 duration-150 cursor-pointer'/> 
        </div>
        <div className='font-semibold text-2xl sm:text-3xl mt-4 tracking-wide'>Mr. Hacker</div>
        <div className='font-semibold text-gray-500 tracking-tight sm:tracking-wide'>Web Developer</div>
        <div className='mt-4 hover:bg-pink-900 bg-pink-500 p-2 px-2 rounded-lg text-white w-72 sm:w-80 flex flex-col items-center tracking-wider cursor-pointer'>Instagram</div>
        <div className='mt-2 hover:bg-pink-900 bg-pink-500 p-2 px-2 rounded-lg text-white w-72 sm:w-80 flex flex-col items-center tracking-widest cursor-pointer'>Fcebook</div>
        <div className='mt-2 hover:bg-pink-900 bg-pink-500 p-2 px-2 rounded-lg text-white w-72 sm:w-80 flex flex-col items-center tracking-widest cursor-pointer'>GitHub</div>
        <div className='mt-2 hover:bg-pink-900 bg-pink-500 p-2 px-2 rounded-lg text-white w-72 sm:w-80 flex flex-col items-center tracking-widest cursor-pointer'>Linkedin</div>
        </div>
      </div>

    </>
  );
}

export default App;
